import React, {useState, useEffect} from "react";
import io from 'socket.io-client';
import Message from './Message';
import AddNewMessage from "./AddNewMessage";
import { useSelector } from 'react-redux';

const Chat = () => {
    let ENDPOIND = "localhost:5000";
    let socket;
    socket = io(ENDPOIND);

    const [message, setMessage] = useState([]);
    const [messages, setMessages] = useState([]);

    useEffect(() => {
      socket.on('add message', data => {
        setMessages([data]);
      });
    })

    const store = useSelector(state => state.user.email);

    function addMessage(title) {
      console.log(title);
      setMessage(message.concat([{
        title,
        id: Date.now(),
        username: store
      }]))
    }

    const sendMessage = (event) => {
      event.preventDefault();
      addMessage(message);
      socket.emit('send message', message)
    }

  return (
    <div>
      <h2>Chat form</h2>
      <Message messages={messages}/>
      <AddNewMessage onCreateMessage={addMessage} sendMessage={sendMessage} message={message} setMessage={setMessage}/>
    </div>
  )
}

export default Chat;
