import React, {useState, useEffect} from "react";
import io from 'socket.io-client';
import Messages from './Messages/Messages';
import AddNewMessage from "./AddNewMessage";
import { useSelector } from 'react-redux';

const Chat = () => {
    let ENDPOIND = "localhost:5000";
    let socket;
    socket = io(ENDPOIND);

    const [message, setMessage] = useState([
      
    ]);
    const [messages, setMessages] = useState([{title: 'hi', id: '2'}]);

    const store = useSelector(state => state.user.email);

    useEffect(() => {
      socket.on('add message', (data) => {
        console.log(data)
        setMessages([...messages, message ]);
      });
      return () => {
        socket.emit('disconnect');

        socket.off();
      }
    }, [messages])

    const sendMessage = (event) => {
      event.preventDefault();
      if(message) {
        socket.emit('send message', message, () => setMessage(''));
      }
    }


  return (
    <div>
      <h2>Chat form</h2>
      <Messages messages={messages}/>
      <AddNewMessage setMessage={setMessage} sendMessage={sendMessage}/>
    </div>
  )
}

export default Chat;
