import React from 'react';

const AddNewMessage = () => {

  const [message, setMessage] = useState('');


  function onSubmit(e) {
    e.preventDefault();

    setMessage('');
  }

  
  return (
    <div className='message__form'>
      <form onSubmit={onSubmit}>
        <textarea
          type="text"
          value={message}
          onChange={e => setMessage(e.target.value)}
          />
        <button type="submit"/>
      </form>
    </div>
  )
}

export default AddNewMessage;
