import { AUTHORIZE } from "../action-types";

const initialState = {
  user: {
    login: null,
    isAuth: false,
    sessid: null
  }
};

function rootReducer(state = initialState, action) {
  console.log(3, action);

  if (action.type === AUTHORIZE) {
    const login = localStorage.setItem('login', action.payload.login);
    const isAuth = localStorage.setItem('isAuth', action.payload.isAuth);
    const sessid = localStorage.setItem('sessid', action.payload.sessid);
    console.log(4);
    return Object.assign({}, state, {
      user: {
        login: localStorage.getItem('login'),
        isAuth: localStorage.getItem('isAuth'),
        sessid: localStorage.getItem('sessid')
      }
    });
  }
  return state;
}

export default rootReducer;
