import React, { useState } from 'react';

const AddNewMessage = ({ sendMessage, message, setMessage }) => {

  return (
    <div className='message__form'>
      <form>
        <input
          type="text"
          value={message}
          onChange={({ target: { value } }) => setMessage(value)}
          />
        <button type="submit" onClick={e => sendMessage(e)}>Отправить </button>
      </form>
    </div>
  )
}

export default AddNewMessage;
