import React, {useState} from "react";
import io from 'socket.io-client';
import Message from './Message';
import AddNewMessage from "./AddNewMessage";
import { useSelector } from 'react-redux';

const Chat = () => {
    let ENDPOIND = "localhost:5000";
    let socket;
    socket = io(ENDPOIND);

    const [message, setMessage] = useState([
      {id: 1, title: 'Привет мир'},
      {id: 2, title: 'И тебе привет'},
    ]);
    
    const [username, setUsername] = useState('')

    const store = useSelector(state => state.user.email);

    function addMessage(title) {
      setUsername(store);
      setMessage(message.concat([{
        title,
        id: Date.now(),
        username
      }]))
    }
  return (
    <div>
      <h2>Chat form</h2>
      <Message messages={message}/>
      <AddNewMessage onCreateMessage={addMessage}/>
    </div>
  )
}

export default Chat;
