import React, {useState} from "react";
import io from 'socket.io-client';
import Message from './Message';
import AddNewMessage from "./AddNewMessage";
import messageItems from "./MessageItem";

const Chat = () => {
    // socket = io(ENDPOIND);
    let socket;
    // let ENDPOIND = "localhost:5000";

    const [message, setMessage] = React.useState([
      {id: 1, title: 'Привет мир'},
      {id: 2, title: 'И тебе привет'},
    ])

    function addMessage(title) {
      setMessage(message.concat([{
        message,
        id: Date.now(),
      }]))
    }

  return (
    <div>
      <h2>Chat form</h2>
      <Message message={messageItems}/>
      <AddNewMessage onCreateMessage={addMessage}/>
    </div>
  )
}

export default Chat;
