import React, {Component} from "react";
import axios from "axios";


export default class Join extends Component {
  constructor(props) {
    super(props);

    this.onChangeLogin = this.onChangeLogin.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onChangePasswordConfirm = this.onChangePasswordConfirm.bind(this);

    this.onSubmit = this.onSubmit.bind(this);


    this.state = {
      login: '',
      password: '',
      passwordConfirm: '',
      meserror: ''
    }
  }

  changeError(meserror) {
    this.setState({meserror});
  }

  handleChange(e) {
    this.props.changeError();
  }


  onChangeLogin(e) {
    this.setState({
      login: e.target.value,
    })
  }

  onChangePassword(e) {
    this.setState({
      password: e.target.value,
    })
  }

  onChangePasswordConfirm(e) {
    this.setState({
      passwordConfirm: e.target.value,
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const user = {
      login: this.state.login,
      password: this.state.password,
      passwordConfirm: this.state.passwordConfirm
    }


       // Store hash in your password DB.

    axios.post('http://localhost:5000/user/add', user)
      .then(res => {
          console.log(res.data.error)
         this.handleChange.bind(this);
         this.setState({title: res.data.error});
      });

    this.setState({
      login: '',
      password: '',
      passwordConfirm: ''
    })
  }

  render () {
    return (
      <div>
        <div className="container__error" >{this.props.meserror}</div>
        <form onSubmit={this.onSubmit} className="form form-registration">
          <div className="form__group">
            <label className="form__label">Логин: </label>
            <input  type="text"
                required
                className="form__input"
                value={this.state.login}
                onChange={this.onChangeLogin}
                />
          </div>
          <div className="form__group"> 
            <label className="form__label">Пароль: </label>
            <input  type="password"
                required
                className="form__input"
                value={this.state.password}
                onChange={this.onChangePassword}
                />
          </div>
          <div className="form__group"> 
            <label className="form__label">Повторите пароль: </label>
            <input  type="password"
                required
                className="form__input"
                value={this.state.passwordConfirm}
                onChange={this.onChangePasswordConfirm}
                />
          </div>
          <div className="form__group submit">
            <button className="form__input submit" type="submit">Отправить</button>
            {/* <input type="submit" value="Create User" className="form__input btn btn-primary" /> */}
          </div>
        </form>
      </div>
    )
  }
}
