import { AUTH_CHANGE_LOGIN, AUTH_CHANGE_SESSION } from './actions';

const defaultState = {
    login: null,
    auth: false
};

export const authReducer = (state = defaultState, action) => {
    switch ( action.type ) {
        case AUTH_CHANGE_LOGIN:
            return {
                ...state,
                login: action.payload
            }
            
        case AUTH_CHANGE_LOGIN:
            return {
                ...state,
                session: action.payload
            }
    }

    return state;
};
